    public void fonts(){
    try{
            File nosifer = new File("src/resources/fonts/Nosifer-Regular.ttf");
            File cursive = new File("src/resources/fonts/ClickerScript-Regular.ttf");
            File zillaMI = new File("src/resources/fonts/ZillaSlab-MediumItalic.ttf");
            File medium = new File("src/resources/fonts/ZillaSlab-Medium.ttf");
            File medium2 = new File("src/resources/fonts/ZillaSlab-Medium.ttf");
            File regular = new File("src/resources/fonts/ZillaSlab-Regular.ttf");
            Font font = Font.createFont(Font.TRUETYPE_FONT, nosifer).deriveFont(40f);
            Font font1 = Font.createFont(Font.TRUETYPE_FONT, cursive).deriveFont(60f);
            Font p2 = Font.createFont(Font.TRUETYPE_FONT, medium).deriveFont(28f);
            Font cont = Font.createFont(Font.TRUETYPE_FONT, medium2).deriveFont(20f);
            Font h = Font.createFont(Font.TRUETYPE_FONT, zillaMI).deriveFont(40f);
            Font txt = Font.createFont(Font.TRUETYPE_FONT, regular).deriveFont(20f);
            TitleMe.setFont(font);
            dere.setFont(font1);
            intro.setFont(p2);
            h2.setFont(h);
            h22.setFont(h);
            tx1.setFont(txt);
            tx2.setFont(txt);
            tx3.setFont(txt);
            face.setFont(cont);
            map.setFont(cont);
            web.setFont(cont);
            tell.setFont(cont);
        }catch(Exception exp){
        JOptionPane.showConfirmDialog(null, exp);
        }
    
    }
