    private void serActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_serActionPerformed
        String sql = "SELECT exp_py_no, exch.ch_no,xp.amount, concat(a.acc_no,',',a.acc_name) as accNomb FROM expense_charge exch,accounts a,expense_peyment xp WHERE exch.ch_no = xp.ch_no and a.acc_no = xp.acc_no and exp_py_no ='"+serTex.getText()+"'";
        co.search(sql);
        //SimpleDateFormat sm = new SimpleDateFormat("yyyy-mm-dd");
        //date = sm.format(reg_date.getDate());
        try{

            if(co.rs.next()){
                id = co.rs.getString("exp_py_no");
                String selected = co.rs.getString("ch_no");
                charCom.setSelectedItem(selected);
                amount.setText(co.rs.getString("amount"));
               String selected1 = co.rs.getString("accNomb");
                accCom.setSelectedItem(selected1);
                serTex.setText("");
            }else{
                JOptionPane.showMessageDialog(null, "record not found");
            }
        }catch(Exception exp){
            JOptionPane.showMessageDialog(null,exp.getMessage());
        }
    }//GEN-LAST:event_serActionPerformed

