    private void saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveActionPerformed
        // TODO add your handling code here:
        //        SimpleDateFormat sm = new SimpleDateFormat("yyyy-MM-dd");
        //        date = sm.format(reg_date.getDate());
        if(charCom.getSelectedItem() != null || amount.getText() != "" || accCom.getSelectedItem() != "" ){

            String oper = "insert";
            String sql = "call expPyment_pro('"+ charg+"','"+amount.getText()+"','"+accNumber+"','"+oper+"',null)";
            co.setSQL(sql);
            clear();
            display();
        }
        else{
            JOptionPane.showConfirmDialog(null, "Please Fill all the blanks");
        }
    }//GEN-LAST:event_saveActionPerformed

    private void update1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_update1ActionPerformed

        String oper = "update";
        String sql = "call expPyment_pro('"+ charg+"','"+amount.getText()+"','"+accNumber+"','"+oper+"','"+id+"')";
        co.setSQL(sql);
        clear();
        display();
    }//GEN-LAST:event_update1ActionPerformed

