    private void delActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_delActionPerformed
       if(id == null){
       JOptionPane.showMessageDialog(null, "Please Search Valid Id, Then delete");
       }else{
       if(JOptionPane.showConfirmDialog(null, "Mahubtaa!")==JOptionPane.YES_OPTION){
           String oper = "delete";
           String sql = "call peo_pro('"+ name.getText()+"','"+tell.getText()+"','"+addN+"','"+oper+"','"+id+"')";
          co.setSQL(sql);
          //display();
          clear();
        }
       }
    }//GEN-LAST:event_delActionPerformed

    private void updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateActionPerformed
       if(id== null){
            JOptionPane.showMessageDialog(null, "Please Search Valid Id, Then delete");
        }else{
        String oper = "update";
       String sql = "call peo_pro('"+ name.getText()+"','"+tell.getText()+"','"+addN+"','"+oper+"','"+id+"')";
      co.setSQL(sql);
      //display();
      clear();
       }
    }//GEN-LAST:event_updateActionPerformed

