    private void serActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_serActionPerformed
       String sql = "select people_no,name,tell, concat(a.add_no,',',a.village,',',a.zone) as addNumb from address a, people p where a.add_no = p.add_no and people_no ='"+serch.getText()+"' or p.name ='"+serch.getText()+"' ";
      co.search(sql);
      try{
      if(co.rs.next()){
      id = co.rs.getString("people_no");
      name.setText(co.rs.getString("name"));
      tell.setText(co.rs.getString("tell"));
      String selected = co.rs.getString("addNumb");
      addNo.setSelectedItem(selected);
      serch.setText("");
      }else{
      JOptionPane.showMessageDialog(null, "record not found");
      }
      }catch(Exception exp){
      JOptionPane.showMessageDialog(null,exp.getMessage());
      }
    }//GEN-LAST:event_serActionPerformed

