    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(id == null){
        JOptionPane.showMessageDialog(null, "Please search valid ID Then Tap Delete Buttom.");
        }else{
        String sql = "call deleteUser_pro('"+id+"')";
          co.setSQL(sql);
          serch.setText("");
          display();
        }
          
    }//GEN-LAST:event_jButton1ActionPerformed

    private void serActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_serActionPerformed
        String sql = "select user_no from users where user_no ='"+serch.getText()+"'";
        co.search(sql);
        try{
            if(co.rs.next()){
                id = co.rs.getString("user_no");
                serch.setText("");
            }else{
                JOptionPane.showMessageDialog(null, "record not found! choose Valid ID");
            }
        }catch(Exception exp){
            JOptionPane.showMessageDialog(null,exp.getMessage());
        }
    }//GEN-LAST:event_serActionPerformed

