    private void signinBottActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signinBottActionPerformed
        // TODO add your handling code here:
        Codes co = new Codes();
        String sql = "select * from users where user_name ='"+usName.getText()+"'and pass='"+usPass.getText()+"'";
       co.search(sql);
      try{
      if(co.rs.next()){
      DashBoard home = new DashBoard();
      home.show();
      this.hide();
      
      home.userName.setText(co.rs.getString("user_name"));
      home.userType.setText(co.rs.getString("user_type"));
      if(home.userType.getText().equalsIgnoreCase("user")){
      home.rep2.setEnabled(false);
      home.usersBtn.setEnabled(false);
      home.fan.setEnabled(false);
      }
      else if(home.userType.getText().equalsIgnoreCase("Finance")){
          home.rep2.setEnabled(false);
          home.usersBtn.setEnabled(false);
      }
      
      }else{
      JOptionPane.showMessageDialog(null, "Invalid user or password");
      }
      }catch(Exception exp){
      JOptionPane.showMessageDialog(null,exp.getMessage());
      }
    }//GEN-LAST:event_signinBottActionPerformed

