    private void signinBottMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_signinBottMousePressed
        if(usName.getText()=="" && usPass.getText()== ""){
        signinBott.setBackground(Color.red);
        }
        else{
        signinBott.setBackground(Color.green);
        }
    }//GEN-LAST:event_signinBottMousePressed

    private void jCheckBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBox1ActionPerformed
        // TODO add your handling code here:
        if(jCheckBox1.isSelected()){
        usPass.setEchoChar((char)0);
        }else{
        usPass.setEchoChar('*');
        }
    }//GEN-LAST:event_jCheckBox1ActionPerformed

